mapper.update:
UPDATE customer
SET customer_pw = #{newPassword}
WHERE
customer_email = #{customerEmail}

UPDATE customer
SET
customer_pw = #{customerPw}
WHERE customer_id = #{customerId}

mapper.select:

SELECT *
FROM (
    SELECT rownum rn, TMP.*
    FROM (
        SELECT *
        FROM customer
        ORDER BY customer_id DESC
    ) TMP
) WHERE rn BETWEEN #{startRow} AND #{endRow}

mapper.delete:
DELETE FROM pick
WHERE
res_no = #{resNo} AND customer_id = #{customerId}

mapper.select:
SELECT COUNT(*)
FROM pick
WHERE customer_id = #{customerId} AND res_no = #{resNo}

SELECT COUNT(*)
FROM pick
WHERE res_no = #{resNo}
