mapper.insert:
insert into payment(
	payment_no, payment_rez_no, payment_customer,
	payment_tid, payment_name, payment_price,
	payment_remain
)
values (
	#{paymentNo}, #{paymentRezNo}, #{paymentCustomer},
	#{paymentTid}, #{paymentName}, #{paymentPrice},
	#{paymentRemain}	
)

mapper.select:
select * from payment

	where payment_customer = #{paymentCustomer}

order by payment_no asc

mapper.update:
update payment
set payment_status = '예약취소',
payment_price=#{paymentPrice}
where payment_no = #{paymentNo}
