mapper.select:
insert into qna(
	qna_no, qna_writer, qna_category, qna_title, qna_answer
)
values (
	#{qnaNo}, #{qnaWriter}, #{qnaCategory}, #{qnaTitle}, #{qnaAnswer}
)

choose.when:

select * from (
    select rownum rn, TMP.* from (
        select * from qna
        where instr(qna_title, #{keyword}) > 0
        order by qna_no desc
    ) TMP
) where rn between #{startRow} and #{finishRow}

choose.otherwise:

select * from (
    select rownum rn, TMP.* from (
        select * from qna
        order by qna_no desc
    ) TMP
) where rn between #{startRow} and #{finishRow}

mapper.update:
   		 update qna
   		 set
            qna_category = #{qnaDto.qnaCategory},
            qna_title = #{qnaDto.qnaTitle},
            qna_answer = #{qnaDto.qnaAnswer}
	    where qna_no = #{qnaNo}
