mapper.select:
SELECT *
FROM (
    SELECT rownum AS RN, A.*
    FROM (
        SELECT *
        FROM reservation_list
        WHERE rez_customer_id = #{rezCustomerId}
        ORDER BY rez_no DESC
    ) A
)
WHERE RN BETWEEN #{startRow} AND #{finishRow}

select count(*) from reservation 
where rez_customer_id = #{rezResNo}
and rez_clock_no = #{rezClockNo}
and rez_seat_no = #{rezSeatNo}
